import React from "react";
import '../App.css';

import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';

const Login = () => {
    const [name, setName] = React.useState('');
    return (
        <>
            <div id="loginBox">
                <Box
                    display="flex" // Enables flexbox
                    flexDirection="column" // Stack children vertically
                    alignItems="center"
                    sx={{
                        '& > :not(style)': { m: 1, width: '300px' },
                        '& label.MuiInputLabel-root': {
                            color: 'white', // Color of the label when unfocused
                        },
                        '& .MuiOutlinedInput-root': {
                            '& fieldset': {
                                borderColor: 'white', // Color of the border
                                borderWidth: '2px',
                            },
                            '&:hover fieldset': {
                                borderColor: 'orange', // Color of the border on hover
                            },
                            '&.Mui-focused fieldset': {
                                borderColor: '#B16CFF', // Color of the border when the TextField is focused
                            },
                            '& input': {
                                color: 'white', // Color of the input text
                            },
                        },
                    }}
                    component="form"
                    noValidate
                    autoComplete="off"
                >
                    <TextField
                        id="outlined-username"
                        label="Username"
                        value={name}
                        onChange={(event) => {
                        setName(event.target.value);
                        }}
                    />
                    <TextField
                        id="outlined-password"
                        label="Password"
                        value={name}
                        onChange={(event) => {
                        setName(event.target.value);
                        }}
                    />
                    <Stack
                        spacing={2}
                        direction="row"
                        display="flex" // Enables flexbox
                        justifyContent="center"
                        sx={{
                            '& > :not(style)': { m: 1, width: '300px' },
                        }}
                    >
                        <Button 
                            variant="outlined"
                            sx={{
                                color: '#B16CFF', // Change text color
                                borderColor: 'white', // Change border color
                                '&:hover': {
                                    backgroundColor: 'rgba(0, 128, 0, 0.04)', // Optional: change background color on hover
                                    borderColor: 'darkgreen', // Optional: change border color on hover
                                },
                            }}
                        >
                            Login
                        </Button>
                    </Stack>
                </Box>
            </div>
        </>
    );
}

export default Login;
