import React from "react";

import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';

const Login = () => {
    const [name, setName] = React.useState('Cat in the Hat');
    return (
        <Box
            display="flex" // Enables flexbox
            flexDirection="column" // Stack children vertically
            alignItems="center" // Center content horizontally
            sx={{
                '& > :not(style)': { m: 1, width: '25ch' },
                '& label.MuiInputLabel-root': {
                    color: 'white', // Color of the label when unfocused
                },
                '& .MuiOutlinedInput-root': {
                    '& fieldset': {
                        borderColor: 'white', // Color of the border
                        borderWidth: '2px', // Increase border width
                    },
                    '&:hover fieldset': {
                        borderColor: 'orange', // Color of the border on hover
                    },
                    '&.Mui-focused fieldset': {
                        borderColor: '#B16CFF', // Color of the border when the TextField is focused
                    },
                    '& input': {
                        color: 'white', // Color of the input text
                    },
                },
            }}
            component="form"
            noValidate
            autoComplete="off"
        >
            <TextField
                id="outlined-username"
                label="Username"
                value={name}
                onChange={(event) => {
                setName(event.target.value);
                }}
            />
            <TextField
                id="outlined-password"
                label="Password"
                value={name}
                onChange={(event) => {
                setName(event.target.value);
                }}
            />
        </Box>
    );
}

export default Login;
